css:
body {
    font-family: Arial, sans-serif;
    margin: 10px;
    margin-top: 3%;
    padding: 0;
    background-color: #f9f9f9;
    color: #333;
}

.payslip {
    width: 100%;
    height: 100%;
    margin: 20px auto;
    padding: 10px;
    border: 2px solid #00BFFF; /* Sky Blue */
    position: relative;
    background-color: rgba(255, 255, 255, 1);
}

.payslip::before {
    content: "";
    position: absolute;
    top: -53;
    left: 5;
    width: 50%;
    height: 50%;
    background-image: url('/files/WhatsApp_Image_2024-09-18_at_18.08.40__2_-removebg-preview.png'); /* Your MTSS logo */
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    opacity: 0.15;
    z-index: 0;
    transform: translate(50%, 50%);
}

.logo-div {
    width: 15%;
    height: 95px;
    text-align: center;
    border: 2px solid #D4AF37; /* Gold border */
    background-color: rgba(212,175,55,0.05); /* Subtle gold tint */
    margin-top: 10px;
}

.heading {
    display: flex;
}

.line-div {
    width: 100%;
    text-align: center;
    margin-top: 5px;
    margin-left: -15px;
}

.mh {
    text-align: right;
}

.pay-month {
    padding-bottom: 25px;
}

.details, .details-2 {
    display: flex;
    min-width: 100%;
    padding: 10px;
}

.light-1, .light-3 {
    display: flex;
    flex-direction: column;
    width: 70%;
    padding-left: 5px;
    background: linear-gradient(90deg, rgba(0,191,255,0.1), rgba(255,255,255,1));
    border-radius: 8px;
}

.light-2, .light-4 {
    display: flex;
    flex-direction: column;
    width: 55%;
    padding-left: 5px;
    background: linear-gradient(90deg, rgba(255,255,255,1), rgba(0,191,255,0.1));
    border-radius: 8px;
}

.details-basic {
    display: flex;
}

.basic-align-txt {
    width: 40%;
}

.basic-align-amt {
    text-align: right;
    width: 60%;
    padding-right: 5px;
}

.basic {
    display: flex;
    width: 100%;
}

.align-txt {
    width: 50%;
    text-align: left;
}

.align-amt {
    width: 50%;
    text-align: right;
    padding-right: 5px;
}

.credential, .bank-details {
    display: flex;
    margin-top: 5px;
    min-width: 100%;
}

.main-earning-heading,
.main-deduction-heading {
    text-align: center;
    background: radial-gradient(circle, rgba(255,255,255,0.85) 3%, #00BFFF 100%);
    color: #000;
    font-weight: bold;
    padding: 5px;
    border-radius: 8px;
}

.main-earnings, .main-deductions {
    border-radius: 12px;
    padding: 2px;
    margin: 8px;
}

.e-cal, .d-cal {
    display: flex;
    padding-bottom: 5px;
    margin: 0 10px;
}

.ed-1, .ed-2, .dd-1, .dd-2 {
    width: 50%;
    padding: 7px;
    border-radius: 12px;
    background-color: rgba(0, 191, 255, 0.08);
    margin: 0 2px;
}

.gross, .total, .netpay-div {
    text-align: center;
    background-color: #B22222; /* Red Ribbon Color */
    color: white;
    padding: 10px 22px;
    margin-left: 50%;
    margin-top: 5px;
    border-radius: 8px;
    border-top: 1px solid #990000;
    font-weight: bold;
}

.prep {
    width: 50%;
    text-align: left;
    padding-left: 5px;
    padding-top: 5px;
    margin: 15px;
}

.system-gen {
    padding-top: 10px;
}
